INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1525', '17', '36501001525', '藏蜜（纯野生沙棘果秘制）', '+', '0', '88', '', '1000', '0.000', '90.00', '75.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">　藏蜜天然野花蜜含多种青藏高原特有的珍贵的天然野花（如野薄荷、野党参、野藿香、野荆棘等），产量稀少，极为珍贵。天然野花蜜保留了蜂蜜中的天然酶和原有的营养物质。富含更多维生素、矿物质、氨基酸、酶类。芳香甜润，口味极佳。属于无污染原生态天然食品。 　　藏蜜纯正油菜蜜富含多种维生素、矿物质、氨基酸等，芳香甜润，口感粘滑。纯正油菜花粉富含多种维生素、矿物质、氨基酸、酶类、叶酸以及微量元素和生物活性物质。被誉为\"浓缩营养库\"、 \"浓缩的微型天然药剂\"、\"能吃的化妆品\"等。实验证明花粉对男性前列腺炎有一定的抑制和治疗作用。口感香甜，口味微酸，是大自然中营养最全面，最丰富也是食素者首选的天然食品之一。藏蜜喀蜂蜂王浆由喀尔巴仟蜂所酿造的，富含多种有益人体健康的特殊物质，是国内最高品质的蜂王浆之一！具有增强抵抗力及免疫功能、预防癌症及抗肿瘤、抗衰老、消炎抗菌作用</span></p><br /></p>', 'images/201204/thumb_img/1525_thumb_P_1334338588648.jpg', 'images/201204/goods_img/1525_P_1334338588439.jpg', 'images/201204/source_img/1525_P_1334338589538.jpg', '1', '', '1', '1', '0', '0', '1334338588', '100', '0', '0', '0', '0', '0', '0', '1334338588', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1526', '17', '36501001526', '伊犁黑蜂蜂蜜', '+', '0', '89', '', '1000', '0.000', '45.60', '38.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">蜂蜜又叫冬酿。主要是因为蜜蜂采集和酿造的艰辛。宋代大词人苏辙“井底屠酥浸旧方，床头冬酿压琼浆”意思是：酒窖里的按照传统的方式炮制的屠苏酒，再加上点蜂蜜，她的滋味超过了神仙喝的美酒”。词中的冬酿就是特指蜂蜜。　   采蜜的蜜蜂\n　　蜂蜜是由蜜蜂采集植物蜜腺分泌的汁液酿成。中国大部分地区均有生产。以稠如凝脂、味甜纯正、清洁无杂质、不发酵者为佳。蜂蜜的主要成分为糖类，其中60%～80%是人体容易吸收的葡萄糖和果糖，主要作为营养滋补品、药用和加工蜜饯食品及酿造蜜酒之用，也可以替食糖作调味品。 　　蜂蜜是一种天然食品，味道甜蜜，所含的单糖，不需要经消化就可以被人体吸收，对妇、幼特别是老人更具有良好保健作用，因而被称为“老人的牛奶”。 　　蜂蜜是一种营养丰富的天然滋养食品，也是最常用的滋补品之一。据分析，含有与人体血清浓度相近的多种无机盐和维生素、铁、钙、铜、锰、钾、磷等多种有机酸和有益人体健康的微量元素，以及果糖、葡萄糖、淀粉酶、氧化酶、还原酶等，具有滋养、润燥、解毒、美白养颜、润肠通便之功效。 　　蜂蜜是糖的过饱和溶液，低温时会产生结晶，生成结晶的是葡萄糖，不产生结晶的部分主要是果糖。  洋槐蜜\n蜂蜜结晶与蜜种、储存条件有关。一般蜂蜜在13摄氏度以下结晶，而油菜蜜、椴树蜜在13-14摄氏度就会结晶，洋槐蜜则不太容易结晶。枣花蜜虽属不易结晶的品种，但如有少量结晶，仍属自然现象，请放心食用。 蜂蜜一般存于阴凉的地方，但是温度不宜过低，蜂蜜一般在10摄氏度时，会发生结晶，不利于食用。所以不宜放入冰箱。 </span></p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334338591', '100', '0', '0', '0', '0', '0', '0', '1334338591', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1527', '17', '36501001527', '伊犁黑蜂蜂蜜巢', '+', '0', '89', '', '1000', '0.000', '50.40', '42.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">蜂巢素简介\n是利用科学的方法，提取蜜蜂巢中的有效成分，加进上等蜂蜜精制而成，纯天然品质。早在两千年前，民间已认识到蜂巢的作用，在公元前的《神农本草经》中，就有“蜜蜡（蜂巢）味甘、微温、无毒。主治下痢脓血，补中，续绝伤金疮，益气”的记载，应用蜂巢治疗疮疖和鼻炎也有很长的历史。现代技术分析表明，蜂巢提取物的成份十分复杂，主要成分有脂肪酸、鞣质、甙类、蛋白质、氨基酸、牛磺酸、酶类和微量元素等，还有许多成份未明的物质。\n  蜂巢是蜜蜂繁育后代贮备粮食的场所，蜂巢内保留着蜂茧衣（类似动物胎盘）、蜂胶、蜂蜡等多种特殊物质，蜂巢素由此精制而成。蜂巢素含有蜂胶、蜂蜡、树脂、油脂、色素、鞣质、糖类、有机酸、脂肪酸、甙类及铁、钙、铜、钾等微量元素。 医药科学研究发现，蜂巢素对于治疗肝炎、鼻炎、鼻窦炎、鼻敏感、慢性和过敏性鼻炎、风湿性关节炎等有良好效果。可迅速缓解因各类鼻炎引起的鼻塞、流涕、头痛、头晕、咽喉不适等症状。蜂巢素深受鼻炎患者的好评。（坚持服用效果更好） \n  同时还有在《本草纲目》中就记载，蜂蜜外抹对鼻炎也有较好效果。如果配合蜂巢素使用，用来涂抹鼻腔，一天2次．睡前涂抹一次，效果最好，蜂蜜要求是高浓度优质蜂蜜</span></p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334338591', '100', '0', '0', '0', '0', '0', '0', '1334338591', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1528', '17', '36501001529', '高原野花蜜', '+', '0', '90', '', '1000', '0.000', '29.76', '24.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">蜂蜜是蜜蜂采集植物的花蜜，经过充分酿造而储存在蜂巢内的一种甜而有粘性的液体。其中葡萄糖和果糖共占65%-80%，蔗糖1.7-2.6%，麦芽糖和其它还原糖7.31%，此外，还含有蛋白质、维生素、氨基酸、矿物质等。蜂蜜是一种滋补品，特别适合于老人、儿童、产妇和病后体弱者。李时珍在《本草纲目》中记载：“蜂蜜其如要之功有五：清热也、补中也、解毒也、止痛也、润燥也……与甘草同功”。\n采集云贵高原上野花蜂蜜，无污染自然生态环境下生长，属稀有蜜种，呈琥珀色，蜜质透亮、清爽可口，具有特有的香气。有疏风清热、解毒消肿、健脑明目、清肺化痰的功效。</span></p><br /></p>', '', '', '', '1', '', '0', '1', '0', '0', '1334338591', '100', '0', '0', '0', '0', '0', '0', '1334338591', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1529', '17', '36501001530', '土蜂蜜', '+', '0', '82', '', '1000', '0.000', '201.60', '168.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">我国的中华蜂在民间俗称为土蜂。只有这种蜂产的蜜才叫土蜂蜜。洋蜂（意蜂）是养在方 箱里的，而真正的土蜂是圆桶蜂箱里的，所采的蜜是高寒森林各种植物的野山花蜜，其蜜色泽深、口感清香而浓郁，富含有多种能被人体直接吸收的微量元素，由于酿蜜周期长、蜜源稀少被誉为“蜜之珍品”。 据《本草纲目》中记载，正宗土蜂蜜是药引的首选蜜，堪称“蜜中精品”。 　　土蜂蜜含有丰富的有机酸、蛋白质、维生素、酶和生物活性物质等多种营养成份，具有润肠、润肺、解毒、养颜、护肝、增强人体免疫力和抗衰老等功效。对保护心血管状动脉和营养心肌的作用，改善心肌功能，对血压有调节作用。 　　通常我们所说的蜂蜜，即普通蜂蜜产自洋蜂（又称家蜂，蜂种多来自意大利品种）。其实洋蜂蜜就是早期从国外引进的西洋蜂种采集的蜂蜜，最为有名的为意蜂，也是国内目前蜂群的一个最主要的品种，由于不同时期采集的花蜜成分不同，所以又可分为椴树密，槐树蜜，荆条花蜜，天麻蜜等。由于产蜜周期短，所以蜂蜜浓度都很低，品质营养成分远不如中华蜂蜜。 </span></p><br /></p>', 'images/201204/thumb_img/1529_thumb_P_1334338591497.jpg', 'images/201204/goods_img/1529_P_1334338591535.jpg', 'images/201204/source_img/1529_P_1334338592498.jpg', '1', '', '1', '1', '0', '0', '1334338591', '100', '0', '0', '0', '0', '0', '0', '1334338591', '1', '', '-1', '-1', '', '', '');
