INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1531', '17', '36501001533', '东北黑蜂颐寿春（王浆蜜）', '+', '0', '99', '', '1000', '0.000', '47.64', '39.70', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">1、稀有蜂种东北黑蜂，仅存于我国乃至亚洲目前唯一的国家级蜂种自然保护区，饶河东北黑蜂国家级自然保护区内。\n2、东北黑蜂具有世界上四大著名蜂种的主要优良性状，强壮有力、采集力强、抗病能力强、耐低温等特点都是决定蜂产品优秀品质的关键因素。\n3、经由东北黑蜂采集酿造出的纯天然、原生态的北大荒牌蜂蜜、花粉、蜂胶、蜂王浆等系列蜂产品，均通过中国有机食品组织的认证，产品质量由中国人民保险公司承保，是蜂产品行业中令人骄傲的国家原产地域保护产品</span></p><br /></p>', 'images/201204/thumb_img/1531_thumb_P_1334338600371.jpg', 'images/201204/goods_img/1531_P_1334338600192.jpg', 'images/201204/source_img/1531_P_1334338600326.jpg', '1', '', '1', '1', '0', '0', '1334338600', '100', '0', '0', '0', '0', '0', '0', '1334338600', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1532', '17', '36501001534', '纯正荔枝蜂蜜礼盒', '+', '0', '0', '', '1000', '0.000', '0.00', '0.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\"> 中国荔枝之乡——灵山县六峰蜜馨——六蜂南方蜂业简介\n\n   公司自有蜂场，蜂群均分布在天然绿色无污染的山林之中，从最初到现在已历经20余年，在这20余年的时间里只朝一个方向前进——做好“荔枝之乡”原始天然纯正之蜂蜜。所产之蜜均取自天然成熟之封盖蜜。20余年的专业精心只为做出品质独特的“中国荔枝之乡”独有的天然纯正之蜂蜜。\n\n \n荔枝蜜\n\n  《本草纲目》云：蜂蜜入药之功有五，清热也，补中也，解毒也，润燥也，止痛也。自古以来，人们就把蜂蜜作为一种天然的保健品和美容品。\n\n荔枝蜜是来自荔枝花蜜经过蜜蜂体内加工而成的一种纯天然流体甜汁。含有70%以上果酸和葡萄糖，含有多种维生素、酶、有机酸、氨基酸、常量元素和微量元素等，还含有丰富的矿物质，与人体血液所含的量十分接近，它营养丰富、口味香甜，是天然的滋补营养品。蜂蜜素有“人类健康之友”、“糖中之王”的美称，荔枝蜜更是“蜜中珍品”，其蜜芳香可口，营养丰富，有蜂蜜之清润，无荔枝之燥热。\n\n \n\n \n\n食用方法：\n\n用温水（40度以下）稀释食用。分早、中、晚三次食用，每次20—30克。\n\n保存方法（注意事项）：\n\n存放在清洁、低温、避风、避光、干燥的地方、为避免与金属起化学反应，应采用非金属、无毒塑料容器储存。存放在冰箱的保险层内更佳。\n\n\n \n\n \n\n                       中国荔枝之乡——荔枝蜜、龙眼蜜\n\n \n\n    中国荔枝之乡——灵山县，位于广西南部，属南亚热带季风气候，土地肥沃，山清水秀，日照充足，十分适宜各种热带水果的生长。每当春暖花开的时节，灵山大地荔枝花飘干层浪，蜂飞蝶舞传花粉，清香醉人。漫山遍野的荔枝树，在盛产优质荔枝的同时，孕育了品质优良，纯正上乘的蜂蜜。\n\n \n\n主营：荔枝蜜 龙眼蜜\n</span></p><br /></p>', 'images/201204/thumb_img/1532_thumb_P_1334338602607.jpg', 'images/201204/goods_img/1532_P_1334338602918.jpg', 'images/201204/source_img/1532_P_1334338603772.jpg', '1', '', '1', '1', '0', '0', '1334338602', '100', '0', '0', '0', '0', '0', '0', '1334338602', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1533', '17', '36501001536', '东北黑蜂椴树蜜', '+', '0', '99', '', '1000', '0.000', '32.40', '27.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">椴树，又名菩提树，被日本大和民族称之为神树，主要分布在我国东北地区，是东北黑蜂的主要蜜源。饶河东北黑蜂国家级自然保护区内拥有着丰富的椴树资源，有中国“椴花之乡”的美誉。这里椴树高大、坚实、树冠蓬勃，在北国原始森林有王者之相，椴花小而密，七月花期，喷薄怒放，树冠灿诺满天星斗，蜜汁晶莹饱满，芳香漫野，招致群蜂飞舞。\r椴树蜜，是蜜蜂从椴树花中所采集的。它醇厚甘甜，比一般蜂蜜含有更多的葡萄糖、果糖、维生素、氨基酸、激素、酶及酯类，具有补血、润肺、止咳消渴、促进细胞再生、清热利导、清肝明目、和血补身、止痛解毒、肿胀、防咳嗽、支气管炎、安眠镇静、促进人体生长和机能恢复、增加食欲和止痛等多方面的神奇功效，是蜂蜜中难得的佳品，被誉为“蜂蜜品质之翘楚”。椴树蜜呈特浅琥珀色，又有薄荷的清香味，结晶细腻，同北方上等蜜与南方的荔枝蜜齐名，称之为“南北系两大名蜜”。椴树蜜含有芳香的麝子油，具有镇静抗菌的作用，可广泛应用于镇静、止咳及安眠等，还具有温胃润肠、治疗便秘，消除疲劳的神奇功效。同时也是纯天然的美容品，可润肤、抗皱、保温、促进肌肤生理平衡等。\r我保护区内的蜂场是被欧盟有机食品组织、中国有机食品组织认证的有机蜂场，公司按照中国有机食品的操作规范要求，加工和生产的椴树蜜等产品是纯天然酿造无抗生素残留、无公害、无污染、色正、味香的有机食品。其被广大消费者所传颂，多次荣获国内外各项大奖。\r椴树蜜神奇功效1、血脂、血压调节   2、增强免疫力  、营养滋补抗疲劳   5、护肤养颜  、调节内分泌   6、复合营养剂 、补充维生素、矿物质</span></p><br /></p>', 'images/201204/thumb_img/1533_thumb_P_1334338604435.jpg', 'images/201204/goods_img/1533_P_1334338604491.jpg', 'images/201204/source_img/1533_P_1334338605340.jpg', '1', '', '1', '1', '0', '0', '1334338604', '100', '0', '0', '0', '0', '0', '0', '1334338604', '1', '', '-1', '-1', '', '', '');
