INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1537', '20', '36501001540', '赛品咖啡', '+', '0', '91', '', '1000', '0.000', '15.36', '12.80', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">    咖啡品质决定于所生长的环境、气候和栽培管理技术等多种因素。它的最佳生长环境是纬度低、海拔高、雨量足、阳光适宜，因此北纬15。至北回归线之间是咖啡的理想生长地带。但处于这个地带上其他地区的如美国夏威夷、沙特阿拉伯等，或是海拔低、或是沙漠少雨，都不利于咖啡生长，唯有处于这个地带上的云南南部正好具备了各种条件。据专家测定，小粒种咖啡应种在海拔800~1800米的山地上，若海拔太高则味酸，太低则味苦。\n    云南咖啡多数植于海拔1100米左有的干热河谷地区，所以酸味适中，香味浓郁且醇和。就气候条件而言，云南南部光照时间长，有利于植株的生长及光合作用，而且昼夜温差大，晚上温度低，有利于咖啡养分的积累，所以云南小粒咖啡所含的有效营养成分高于国外的其他咖啡品种。小粒种咖啡容易感染锈病，产生绣病后产量和质量都深受影响。</span></p><br /></p>', 'images/201204/thumb_img/1537_thumb_P_1334338616546.jpg', 'images/201204/goods_img/1537_P_1334338616987.jpg', 'images/201204/source_img/1537_P_1334338617609.jpg', '1', '', '1', '1', '0', '0', '1334338616', '100', '0', '0', '0', '0', '0', '0', '1334338616', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1538', '6', '36501001541', '三七粉', '+', '0', '0', '', '1000', '0.000', '237.60', '198.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">可用于治疗外伤出血、瘀血、胃出血、尿血等各种内、外出血症；扩张血管，溶解血栓，改善微循环，预防和治疗高血脂、胆固醇增高、冠心病、心绞痛、脑溢血后遗症等心脑血管疾病；脂肪肝、肝纤维化等肝病以及失血、产后、久病等原因导致的体虚症</span></p><br /></p>', 'images/201204/thumb_img/1538_thumb_P_1334338620612.jpg', 'images/201204/goods_img/1538_P_1334338620236.jpg', 'images/201204/source_img/1538_P_1334338621670.jpg', '1', '', '1', '1', '0', '0', '1334338620', '100', '0', '0', '0', '0', '0', '0', '1334338620', '1', '', '-1', '-1', '', '', '');
INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1539', '6', '36501001542', '沙河粉', '+', '0', '0', '', '1000', '0.000', '11.40', '9.50', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">清末，沙河镇有一个小店铺，名叫“义和居”，店主名叫樊阿香，已经成婚，还有一个老父亲，叫樊官秀，一家三口以卖白粥油器、饭食为生。日子不算富裕，但也还过得去。　一天，樊阿香吃了些上火的东西，又着了凉，有些发热，头重脚轻，不思饮食。父亲樊官秀很是发愁，便想做些新鲜食品给儿子开开胃。　做什么好呢？想来想去，自己别的不会做，只会用米浆蒸蒸糕做点粉条，但都粗糙大块，口感不怎么样。要想儿子胃口好，还是做点薄的米粉吧。怎样做米粉才能薄呢？他抬头一看，看见平常晒东西用的  沙河粉\n窝篮，心里突然灵光一闪：在上面倒一层薄薄的米浆，蒸熟后不就是一层薄薄的米粉了吗？于是马上洗净窝篮，又浸好米，磨了米浆，烧开了蒸锅里的水后，舀了一勺倒在窝篮上。然后轻轻一摇，让米浆薄薄地摊在上面，放在锅里，用猛火蒸上一两分钟，米浆就熟了，成了薄薄的一片粉皮。樊官秀揭起粉皮，切条，再加上油盐香葱等佐料，一碗香喷喷的米粉做好了。樊阿香一尝十分可口，胃口好多了，边吃边问父亲这粉叫什么名字。樊官秀说：“这是见你不开胃，试着蒸的，还没有名字。”妻子说：“这粉是在沙河创制的，不如就叫沙河粉吧。”樊阿香父子也齐声叫好。第二天，樊阿香便把沙河粉推了出去，十分受欢迎。生意越来越好，樊阿香干脆停了粥、面、饭的制作，把自己的“义和居”改为专营沙河粉。他买来优质的安南米，请人拣去里面的沙子杂质，用白云山水浸上两个多钟头，待米浸软后，又用白云山水磨成米浆，蒸成米粉。由于白云山水水质优良，制成的沙河粉又薄又韧，细滑可口，本地居民，过往客商都喜欢光顾，生意十分红火，连广州的大茶楼也到“义和居”订沙河粉。樊阿香便请人专门挑沙河粉去广州市送货，生意越做越大，邻里小店也争相效仿，沙河粉就成了沙河镇的特产</span></p><br /></p>', 'images/201204/thumb_img/1539_thumb_P_1334338625510.jpg', 'images/201204/goods_img/1539_P_1334338625902.jpg', 'images/201204/source_img/1539_P_1334338625826.jpg', '1', '', '1', '1', '0', '0', '1334338624', '100', '0', '0', '0', '0', '0', '0', '1334338624', '1', '', '-1', '-1', '', '', '');
