INSERT INTO `green_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `goods_advice` ) VALUES  ('1555', '6', '36501001558', '全新有机厨坊黑之麻粉', '+', '0', '29', '', '1000', '0.000', '105.60', '88.00', '0.00', '0', '0', '1', '', '', '<p> <span style=\"line-height:2.5;font-size:14px;\">100％純黑芝麻粉 \n\n◎低溫烘焙、無糖、零膽固醇！\n◎不經榨油，低溫烘焙而成\n●芝麻粉中約含有49公克的不飽和脂肪酸，並富含人體所需的亞麻油酸，是相當適合人體的優良脂肪來源。\n(P=多元不飽和脂肪酸，M=單元不飽和脂肪酸，S=飽和脂肪酸)\n●特選頂級黑芝麻精製而成，低溫烘焙，以維持最佳香氣，並保留完整營養素，口感溫潤、香醇，優於市面一般黑芝麻；有機廚坊-100%黑芝麻粉，營養豐富，可幫助牙齒骨骼正常發育，調整體質，增強體力，適合各個年齡層食用，為日常滋補的最佳良品。\n\n產品規格:500公克∕罐\n全素可用\n\nQ:100%黑芝麻粉为何容易结块？\nA:因为 100%黑芝麻粉是未经炸油直接烤焙后磨粉制作，所以油脂含量比一般黑芝麻粉高，容易形成假结块乃为正常现象，只需用汤匙轻轻搅伴即可散开，并非劣变现象，可安心食用。\n一般芝麻会先经过榨油制作芝麻油，剩下的芝麻壳才制作成芝麻粉，如此可赚取的利润较高，而100%黑芝麻粉不惜成本未经炸油制作，保留较多的亚麻油酸成分，营养价值更高。\n\nQ:黑芝麻与白芝麻的营养有何差异？\nA:芝麻含有丰富的维生素B群、维生素E与镁﹑钾﹑锌及多种微量矿物质，而黑芝麻钙﹑铁的含量远高于白芝麻（每100公克黑芝麻含钙 1456 毫克、铁 24.5 毫克；每100公克白芝麻含钙81毫克、铁8.4毫克），另外黑芝麻也较白芝麻含较多的粗纤维。\n由于100%黑芝麻粉为未经任何调味之纯黑芝麻粉，所以建议搭配有机厨坊其它产品\n一起食用，不但营养更丰富，也更增添香气风味。\n\n食用方法：\n取汤匙一平匙的 100%黑芝麻粉倒入小试喝纸杯，直接试吃。\n使用一般 150c.c.免洗纸杯先取5分满的牛奶或豆浆，再倒入汤匙1平匙的100%黑芝麻粉，搅拌均匀后即可试喝。因 100%黑芝麻粉会越嚼越香，请在嘴内多咀嚼几下，细细品尝其香气。\n\n</span></p><br /></p>', 'images/201204/thumb_img/1555_thumb_P_1334338658666.jpg', 'images/201204/goods_img/1555_P_1334338659216.jpg', 'images/201204/source_img/1555_P_1334338659237.jpg', '1', '', '1', '1', '0', '0', '1334338658', '100', '0', '0', '0', '0', '0', '0', '1334338658', '1', '', '-1', '-1', '', '', '');
